package com.tsing.global.result;import java.io.Serializable;/** * @author Tsing * @version 0.0.1 * @date 2021/8/1 7:20 上午 * @description: 全局返回包装对象 */public class ResultData<T> implements Serializable {    private static final long serialVersionUID = 356106788730169498L;    /**     * 业务错误码     */    private Integer code;    /**     * 信息描述     */    private String message;    /**     * 返回参数     */    private T data;    public ResultData() {    }    private ResultData(ResultStatus resultStatus, T data) {        this.code = resultStatus.getCode();        this.message = resultStatus.getMessage();        this.data = data;    }    /**     * 返回成功：     *   业务状态码     *   描述信息     */    public static ResultData<Void> success() {        return new ResultData<>(ResultStatus.SUCESS, null);    }    /**     * 返回成功：     *   业务状态码     *   描述信息     *   返回值     */    public static <T> ResultData<T> success(T data) {        return new ResultData<T>(ResultStatus.SUCESS, data);    }    /**     * 返回成功：     *   业务状态码     *   描述信息     *   返回值     */    public static <T> ResultData<T> success(ResultStatus resultStatus, T data) {        if(null == resultStatus) {            return success(data);        }        return new ResultData<T>(resultStatus, data);    }    /**     * 返回错误：     *    业务状态码     *    描述信息     */    public static ResultData<Void> failure() {        return new ResultData<>(ResultStatus.ERROR, null);    }    /**     * 返回错误：     *    自定义业务状态码     *    描述信息     */    public static <T> ResultData<T> failure(ResultStatus resultStatus) {        return new ResultData<>(resultStatus, null);    }    public Integer getCode() {        return code;    }    public void setCode(Integer code) {        this.code = code;    }    public String getMessage() {        return message;    }    public void setMessage(String message) {        this.message = message;    }    public T getData() {        return data;    }    public void setData(T data) {        this.data = data;    }    @Override    public String toString() {        return "ResultData{" +                "code=" + code +                ", message='" + message + '\'' +                ", data=" + data +                '}';    }}