package com.tsing.product.api.teambuilding.bo.request;import java.io.Serializable;/** * @author Tsing * @version 0.0.1 * @date 2021/8/1 1:02 下午 * @description: 团建游分页查询对象 */public class TeambildingPage implements Serializable {    private static final long serialVersionUID = 5981076218412292520L;    /**     * 团建游ID     */    private Long id;    /**     * 团建游标题     */    private String name;    /**     * 分页信息     */    private Integer page = 0;    /**     * 分页信息     */    private Integer pageSize = 20;    public TeambildingPage() {    }    public TeambildingPage(Long id, String name, Integer page, Integer pageSize) {        this.id = id;        this.name = name;        this.page = page;        this.pageSize = pageSize;    }    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public Integer getPage() {        return page;    }    public void setPage(Integer page) {        this.page = page;    }    public Integer getPageSize() {        return pageSize;    }    public void setPageSize(Integer pageSize) {        this.pageSize = pageSize;    }}